AND in_id IN
#{item}

select imd_id as sub_imd_id, in_id as sub_in_id, in_code as sub_in_code, od_id as sub_od_id, g_code as sub_g_code,g_id as sub_g_id, quantity_in_stock as sub_quantity_in_stock, unstocked_quantity as sub_unstocked_quantity, sl_id as sub_sl_id,money as sub_money, this_quantity as sub_this_quantity, production_date as sub_production_date, barcode as sub_barcode, remark as sub_remark, create_by as sub_create_by, create_time as sub_create_time, update_by as sub_update_by, update_time as sub_update_time, is_delte as sub_is_delte from crk_im_details
AND in_id IN
#{item}

SELECT a.pd_id, a.po_code, a.g_code, a.purchase_quantity, a.money, a.remark,
b.g_id, b.gt_id, b.g_name, b.g_unit, b.spec_code
FROM order_purchase_details a
LEFT JOIN hp_goods b ON b.g_code = a.g_code
LEFT JOIN order_purchase_order c ON c.po_code = a.po_code


        AND a.po_code = #{poCode}


        AND b.g_name = #{gName}


        AND b.g_unit = #{gUnit}

    AND c.status = 2

