mapper.select:
select out_code
from crk_out_management
order by out_code desc limit 1;

SELECT DISTINCT a.sd_id, a.s_code, a.g_code, a.sales_volume, a.sales_amount, a.remark, b.g_id, b.gt_id, b.g_name, b.g_unit, b.spec_code, (SELECT CASE WHEN SUM(d.item_quantity) IS NULL THEN 0 ELSE SUM(d.item_quantity) END FROM tz_stock d INNER JOIN hp_goods h ON h.g_id = d.g_id WHERE h.g_code = a.g_code) AS kcsl, (SELECT CASE WHEN SUM(e.item_quantity) IS NULL THEN 0 ELSE SUM(e.item_quantity) END FROM crk_out_details e WHERE e.g_code = a.g_code and e.order_id=a.s_code) AS cksl FROM order_d_sales a LEFT JOIN hp_goods b ON b.g_code = a.g_code LEFT JOIN ( SELECT g_id, w_id FROM tz_stock GROUP BY g_id, w_id ) d ON d.g_id = b.g_id LEFT JOIN order_sales_order i ON i.s_code = a.s_code
AND a.s_code = #{sCode}
AND b.g_name = #{gName}
AND b.g_unit = #{gUnit}
AND d.w_id = #{wId}
AND (i.status = 2 || i.status = 3)

SELECT CASE WHEN sum(item_quantity) IS NULL THEN 0 ELSE sum(item_quantity) END AS kcsl
FROM tz_stock
WHERE sl_id = #{slId}
  and g_id = #{gId}

select om_id as sub_om_id, out_out_id as sub_out_out_id, out_id as sub_out_id, order_id as sub_order_id, unit as sub_unit, item_quantity as sub_item_quantity, quantity_shipped as sub_quantity_shipped, undelivered_quantity as sub_undelivered_quantity, curr_out_quantity as sub_curr_out_quantity, batch_number as sub_batch_number, remark as sub_remark, barcode as sub_barcode, create_by as sub_create_by, create_time as sub_create_time, update_by as sub_update_by, update_time as sub_update_time, is_delte as sub_is_delte, sl_id as sub_sl_id, g_code as g_code, g_id as g_id, out_bound as out_bound, not_shipped as not_shipped, money as money from crk_out_details
AND out_id IN
#{item}

select om_id as sub_om_id, out_out_id as sub_out_out_id, out_id as sub_out_id, order_id as sub_order_id, unit as sub_unit, item_quantity as sub_item_quantity, quantity_shipped as sub_quantity_shipped, undelivered_quantity as sub_undelivered_quantity, curr_out_quantity as sub_curr_out_quantity, batch_number as sub_batch_number, remark as sub_remark, barcode as sub_barcode, create_by as sub_create_by, create_time as sub_create_time, update_by as sub_update_by, update_time as sub_update_time, is_delte as sub_is_delte, sl_id as sub_sl_id, g_code as g_code, g_id as g_id, out_bound as out_bound, not_shipped as not_shipped, money as money from crk_out_details
AND out_id IN
#{item}

mapper.update:
update crk_out_management
out_code = #{outCode}, out_type = #{outType}, status = #{status}, out_date = #{outDate}, order_id = #{orderId}, c_id = #{cId}, person_id = #{personId}, contact_details = #{contactDetails}, remark = #{remark}, out_manager = #{outManager}, w_id = #{wId}, reviewer = #{reviewer}, reviewer_time = #{reviewerTime}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, is_delte = #{isDelte},
where out_id = #{outId}

update tz_stock
set number_plans = number_plans + #{itemQuantity}
where g_id = #{gId}
  and sl_id = #{slId}

update tz_stock
set number_plans = number_plans - #{itemQuantity}
where g_id = #{gId}
  and sl_id = #{slId}

choose.when:
update crk_out_management
set status = 3,
reviewer = #{crkOutManagements.reviewer},
reviewer_time = #{crkOutManagements.reviewerTime}
where out_id = #{crkOutManagements.outId}

choose.otherwise:
update crk_out_management
set status = 2,
reviewer = #{crkOutManagements.reviewer},
reviewer_time = #{crkOutManagements.reviewerTime}
where out_id = #{crkOutManagements.outId}

mapper.update:
update tz_stock
set item_quantity = item_quantity - #{itemQuantity}
where g_id = #{gId}
  and sl_id = #{slId}

update order_sales_order set status = 4


        AND s_code IN

            #{item}

